function drawTimer(){if(1==gameState){var t=[],e=[];if(MainHex.ct-MainHex.lastCombo<settings.comboTime)for(var i=0;i<6;i++){var a=MainHex.ct-MainHex.lastCombo;if(!(a<settings.comboTime*(5-i)*(1/6))){t.push(calcSide(i,i+1,1-6*a/settings.comboTime%1,1)),e.push(calcSide(12-i,11-i,1-6*a/settings.comboTime%1,1));break}t.push(calcSide(i,i+1,1,1)),e.push(calcSide(12-i,11-i,1,1))}0!==e.length&&drawSide(e),0!==t.length&&drawSide(t)}}function calcSide(t,e,i,a){t=(t+a)%12,e=(e+a)%12,ctx.globalAlpha=1,ctx.beginPath(),ctx.lineCap="round";var o=settings.rows*settings.blockHeight*(2/Math.sqrt(3))+settings.hexWidth,s=o/2,c=o*(Math.sqrt(3)/2),n=[[3*s/2,c/2],[o,0],[3*s/2,-c/2],[s,-c],[0,-c],[-s,-c],[-3*s/2,-c/2],[-o,0],[-3*s/2,c/2],[-s,c],[0,c],[s,c]].reverse(),r=trueCanvas.width/2+n[t][0],l=trueCanvas.height/2+n[t][1];return[[r,l],[(trueCanvas.width/2+n[e][0]-r)*i+r,(trueCanvas.height/2+n[e][1]-l)*i+l]]}function drawSide(t){0===gameState?ctx.strokeStyle=hexColorsToTintedColors[MainHex.lastColorScored]:ctx.strokeStyle=MainHex.lastColorScored,ctx.lineWidth=4*settings.scale,ctx.moveTo(t[0][0][0],t[0][0][1]),ctx.lineTo(t[0][1][0],t[0][1][1]);for(var e=1;e<t.length;e++)ctx.lineTo(t[e][1][0],t[e][1][1]),ctx.moveTo(t[e][1][0],t[e][1][1]);ctx.closePath(),ctx.fill(),ctx.stroke()}